@cttee_id,
@belong_cttee,
@instructor_id,
@cttee_role,
@cttee_year);



UPDATE `QUDMMS_DB`.`QUDM_TAMS_instructorCtteeRel`(
'cttee_id',
`belong_cttee`,
`instructor_id`,
`cttee_role`,
`cttee_year`)
VALUES(
@'cttee_id',
@belong_cttee,
@instructor_id,
@cttee_role,
@cttee_year);



DELETE FROM `QUDMMS_DB`.`QUDM_TAMS_instructorCtteeRel` WHERE cttee_id = @cttee_id AND 
instructor_id = @instructor_id AND cttee_role = @cttee_role AND cttee_year = @cttee_year;



SELECT `QUDM_TAMS_ctteeInfo`.`lecture_id`,
  `QUDM_TAMS_ctteeInfo`.`cttee_id`,
  `QUDM_TAMS_ctteeInfo`.`cttee_tittle`,
  `QUDM_TAMS_ctteeInfo`.`cttee_year`,
  `QUDM_TAMS_ctteeInfo`.`chair_admin_weight`,
  `QUDM_TAMS_ctteeInfo`.`co_chair_admin_weight`,
  `QUDM_TAMS_ctteeInfo`.`secretary_admin_weight`,
  `QUDM_TAMS_ctteeInfo`.`memeber_admin_weight`,
  `QUDM_TAMS_ctteeInfo`.`cttee_notes`
  FROM `QUDMMS_DB`.`QUDM_TAMS_ctteeInfo`
  WHERE `cttee_tittle`= @cttee_tittle;



DELETE FROM `QUDMMS_DB`.`QUDM_TAMS_ctteeInfo` WHERE cttee_id = @cttee_id;
